<template>
  <div :is="tag" :class="outerClasses">
    <div :is="innerTag" :class="innerClasses">

      <slot></slot>
    </div>
  </div>
</template>

<script>
import classNames from 'classnames';

export default {
  props: {
    tag: {
      type: String,
      default: "div"
    },
    innerTag: {
      type: String,
      default: "div"
    },
    flipped: {
      type: Boolean,
      default: false
    },
    outerClass: {
      type: [String, Array],
      default: ''
    },
    innerClass: {
      type: [String, Array],
      default: ''
    }
  },
  computed: {
    outerClasses() {
      return classNames(
        'card-wrapper',
        this.outerClass
      );
    },
    innerClasses() {
      return classNames(
        'card-rotating effect__click',
        this.flipped && 'flipped',
        this.innerClass
      );
    }
  }
};
</script>

